import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = props => {
	const { isLoggedIn, username } = props;
    const navLinks = isLoggedIn ? (
    <div className="ui teal inverted three item menu">
      <Link to='/groups' className="item">Home</Link>
      <div className='item'>{`Hello, ${username}!`}</div> 
      <Link to='/logout' className="item">Log out</Link>
    </div>
    ) : (
    <div className="ui teal inverted two item menu">
      <Link to='/register' className="item">Register</Link>
      <Link to='/login' className="item">Login</Link>
    </div>
    )
    return (
      <div>{navLinks}</div>
    )
}

export default Navbar;
