import React, { Component } from 'react';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import axios from 'axios';

class Login extends Component {
	state = {
		username: '',
		password: ''
	}
	handleChange = e => {
		this.setState({
			[e.target.name]: e.target.value
		})
	}
	handleSubmit = e => {
		e.preventDefault();
		console.log('Inside submit login');
		axios.post('http://localhost:8080/api/login', {
			username: this.state.username,
			password: this.state.password
		}).then(res => {
			this.props.logIn(res.data[0].username, res.data[0]._id, 'Successfully signed in', 'success')
		}).catch(err => {
			this.props.showAlert('User not found', 'danger')
		})
	}
	render(){
		const { isLoggedIn } = this.props
		if (isLoggedIn) {
			return <Redirect to='/groups' />
		}
		return (
			<div className='container'>
				<form onSubmit={this.handleSubmit} className='ui form'>
					<div className="field">
						<label>Username</label>
						<input type='text' name='username' placeholder='username' 
						onChange={this.handleChange} required />
					</div>
					<div className="field">
						<label>Password</label>
						<input type='password' name='password' placeholder='password' 
						onChange={this.handleChange} required />
					</div>
					<button className='ui teal button'>Log in</button>
				</form>
				<div>
					You don't have an account yet?
					<span><Link to='/register'>Register here</Link></span>
				</div>
			</div>
		)
	}
}

export default Login;
