import React, { Component } from 'react';
import axios from 'axios';

class AddUser extends Component {
	state = {
		search: '',
    suggestions: []
	}
  searchUser = e => {
    this.setState({
      search: this.refs.search.value
    })
    axios.post('http://localhost:8080/api/users', 
      { search: this.state.search, members: this.props.members })
    .then(response => {
      this.setState({
        suggestions: response.data
      })
    }).catch(err => console.log(err))
  }
  deleteSuggestions = () => {
    this.setState({
      search: '',
      suggestions: []
    })
    this.refs.search.value = '';
  }
	render() {
    const { suggestions } = this.state
    const { addMember } = this.props
    const suggestionsList = (
      <div>
        {
          suggestions.map(suggestion => {
            return  <div key={suggestion._id} className='suggestions'
                      onClick={() => { 
                        addMember(suggestion); 
                        this.deleteSuggestions() 
                      } }>
                        {suggestion.username}                        
                    </div>
          })
        }
      </div>
    )
    return (
          <div>
            <input type='text' onChange={this.searchUser} name='search' ref='search' />
            <span onClick={this.searchUser}>Search</span>
            <div>{suggestionsList}</div>
          </div>
			)
	}
}

export default AddUser;
